import React, { useState } from "react";
import Base from "../core/Base";
import { signUp } from "../auth/helper";

const SignUp = () => {

    const [values, setValues] = useState({
        name:"",
        email: "",
        password: "",
        error: "",
        success: false
    })

    const {name, email, password, error, success} = values;

    const handleChange = (name)  => (event) => {
        setValues({...values, error: false, [name]: event.target.value});
    }

    const SignUpForm = () => {
        return (
            <div className="row">
                <div className="col-md-6 offset sm-3 text-left">
                    <form>
                        <div className="form-group">
                            <label className="text-light">Name</label>
                            <input value={name} onChange={handleChange("name")} type="text" />
                        </div>
                        <div className="form-group">
                            <label className="text-light">Email</label>
                            <input value={email} onChange={handleChange("email")} type="text" />
                        </div>
                        <div className="form-group">
                            <label className="text-light">Password</label>
                            <input value={password} onChange={handleChange("password")} type="text" />
                        </div>
                        <button className="btn btn-success btn-block">Submit</button>
                    </form>
                </div>
            </div>
        )
    }

    return (<Base title="Sign Up page" description="This is a user sign up page">
        {SignUpForm()}
        <p>Testing of SIgn up page</p>
    </Base>
    )
}

export default SignUp;
