import React, { useState } from "react";
import Base from "../core/Base";

const SignIn = () => {

    const [values, setValues] = useState({
        email: "",
        password: "",
        error: "",
        success: false,
        loading: false,
        didRedirect: false
    })
    const {email, password, error, success, loading, didRedirect} = values;
    const successMessage = () => {
        return (
            <div className="row">
                <div className="col-md-6 offset-sm-3 text-left">
                    <div className="alert alert-success"
                    style={{display: success ? "" : "none"}}>
                        New account created successfully. Please <Link to="/signin">login now.</Link> 
                    </div>
                </div>
            </div>
        )
    }

    const handleChange = (name)  => (event) => {
        setValues({...values, error: false, [name]: event.target.value});
    }

    const errorMessage = () => {
        return (
            <div className="row">
                <div className="col-md-6 offset-sm-3 text-left">
                    <div className="alert alert-danger"
                    style={{display: error ? "" : "none"}}>
                        All fields are required.
                    </div>
                </div>
            </div>
        )
    }

    const SignInForm = () => {
        return (
            <div className="row">
                <div className="col-md-6 offset-sm-3 text-left">
                    <form>
                        <div className="form-group">
                            <label className="text-light">Email</label>
                            <input value={email} onChange={handleChange("email")} type="text" className="form-control"/>
                        </div>
                        <div className="form-group">
                            <label className="text-light">Password</label>
                            <input value={password} onChange={handleChange("password")} type="password" className="form-control"/>
                        </div>
                        <button className="btn btn-success btn-block" onClick={()=>{}}>Submit</button>
                    </form>
                </div>
            </div>
        )
    }
    return (<Base title="Sign In Page">
        <p>This is sign in page</p>
        {successMessage()}
        {errorMessage()}
        {SignInForm()}
    </Base>
    )
}

export default SignIn;
